INSERT INTO `fd_ask` VALUES ('1', '为什么想赚钱的人有那么多？为什么大多数人还是没赚到钱呢？', '10', '1', '211231', null, '12', '23', '1', '1', null);
INSERT INTO `fd_ask` VALUES ('2', '现实生存发展（钱）和内心找一个平衡。平衡你就快乐。失衡，很多很多很多人都没衡。身份定位，我的问题一直是这里。我一直不认可不支持自己的身份定位  ？', '5', '1', '544445', null, '121', '0', '1', '1', null);
INSERT INTO `fd_ask` VALUES ('3', '您好。本不打算叨扰您，但是还是要小说一句。终于发现，以前学来的 听来的 看来的 经历来的 …都不是无用之功。人习惯找不到自己，其实也许只是时机不够到 ？', '15', '1', '454444', null, '1215', '232', '1', '1', null);
INSERT INTO `fd_ask` VALUES ('4', '去一直想去的大公司面试了两轮，第三轮说面试官出差回来再面，等了半个月打电话问说人事去校招了，十一后回来！我裸辞北京房租挺贵的。心里不平衡又着急又想去，我该怎么办', '7', '2', '222222', null, '1', '115', '1', '1', null);
INSERT INTO `fd_ask` VALUES ('5', '如何看待一个公司，绝大部分人不是上班的，可以理解成“闲”', '24', '2', '666666', null, '2', '4', '1', '1', null);
INSERT INTO `fd_ask` VALUES ('6', '部门上级男和下级「女」通奸。部门上级家属也知道此事，下级女也去男方家里闹过。公司总经理和分管副总都知道此事没有做出动作。下级女和我有直接竞争，现在对我不是很利', '8', '3', '99999', null, '3', '6', '1', '1', null);
INSERT INTO `fd_ask` VALUES ('7', '我从传统公司跳槽到互联网小公司，只有我一个运营（APP，新媒体），有很多事情都是自己摸索在做，领导又是非运营出身，也有很多不懂。目前有点吃力又迷茫，是否该跳槽？', '6', '3', '44848', null, '5', '3', '1', '1', null);
INSERT INTO `fd_ask` VALUES ('8', '摄影工作室 销售就我1个 销售这块只有我懂 有个系统干货培训不错 我要推荐给我老板听吗？怕他们学会销售和管理以后 会找人来替代我 系统是可复制迁移 我工资拿高 ', '23', '3', '311656', null, '46', '6', '1', '1', null);
INSERT INTO `fd_ask` VALUES ('9', '杨老师，你认为孩子马虎的习惯可以改过来么？应该怎么改？有什么好的方法么？', '6', '4', '644', null, '48', '4', '1', '1', null);
INSERT INTO `fd_ask` VALUES ('10', '大二工业工程专业(知识面广但不精)学生。对本专业比较感兴趣，但以此为生又不甘心。所以对于是否考研感到迷茫(若以此为生必考，若不呢？若不，考研感觉浪费时间)。求解', '11', '4', '646464', null, '61', '6', '1', '1', null);
INSERT INTO `fd_ask` VALUES ('11', '经常会在一些场合不会说话，描述一次你曾经不会说话的经历吧？', '3', '5', '646464', null, '0', '6', '1', '1', null);
INSERT INTO `fd_ask` VALUES ('12', '在满负荷工作的情况下，如何挤时间看书？', '12', '10', '31646', null, '2', '3', '1', '1', null);
INSERT INTO `fd_ask` VALUES ('13', '想请教阁下，用户在快问的一个领域下提问（产生了一个订单），假设这个领域下有800个专家。分答是如分配这个订单的呢？（难道800个人凭运气抢单吗？）很好奇，哈哈', '13', '11', '64899', null, '16', '156', '1', '1', null);
INSERT INTO `fd_ask` VALUES ('14', '我有强迫症。注意很是不集中。无法专注。阅读总是分心。找不到灵感。请您教我一些阅读的方法，如何做到高效，专注？', '4', '11', '64464', null, '4', '156', '1', '1', null);
INSERT INTO `fd_ask` VALUES ('15', '我终于悟到了是。为什么一直让你们解除拉黑，因为我想让你们亲自看看，我是怎样一步一步变的厉害 变的很厉害，变的比你们厉害的 ，仅此而已', '5', '11', '311351', null, '8', '14', '1', '1', null);
INSERT INTO `fd_ask` VALUES ('16', '如何系统的学习股市？', '11', '12', '346464', null, '4', '46', '1', '1', null);
INSERT INTO `fd_ask` VALUES ('17', '马上端午节，有一些什么技巧过节，可以过的开心、又有收获？', '14', '13', '31644', null, '6', '5', '1', '1', null);
INSERT INTO `fd_ask` VALUES ('18', '大学开始就只做自己喜欢的事。上社会自然想这样，可很难做到，能做的不爱做，一边做一边痛苦。是否应该放弃内心，追求利益最大化', '21', '14', '546464', null, '48', '165', '1', '1', null);
INSERT INTO `fd_ask` VALUES ('19', '人必须要解决的事情，永远也无法回避的事情是什么（每个阶段都有最重要和次重要的事，但什么事是贯穿始终的），仔细思考举例回答', '5', '14', '64641', null, '6', '16', '1', '1', null);
INSERT INTO `fd_ask` VALUES ('20', '人的命运是怎么一回事情？ 如果把它缩小成一天，每一天真实的过好就行，那人就可以掌控命运。但还有思维不同人思维塑造不同命运', '24', '14', '316161', null, '1', '156', '1', '1', null);
INSERT INTO `fd_ask` VALUES ('21', '本人性别男，看了您一点儿成长史，总结来说就一句话，在自己喜欢的事情上顺便拿点钱，是这样子不 ？', '26', '15', '116161', null, '3', '45', '1', '1', null);
INSERT INTO `fd_ask` VALUES ('22', '32岁，在一家国企做销售八年，各领导对我都很好，2月刚调动到家乡任职，现遇到一个平台和收入都很好的职位，很心动，却同时觉得对企业和领导感情观上过不去，希望指导', '27', '16', '646466', null, '8', '16', '1', '1', null);
INSERT INTO `fd_ask` VALUES ('23', '您好！毕业后在外企做技术9年，去年考了MBA想寻求销售或管理类工作。不料今年失业，工作未定，学校也已开学。不知道还有没有必要去读？我该如何规划？因必须立马决策！', '9', '0', '31212', null, '6', '64', '1', '1', null);
INSERT INTO `fd_ask` VALUES ('24', '女，26，父母兄长希望我在老家找个稳定工作上班，我想出去闯一闯。现在沟通不畅僵持不下。我该怎么办？', '7', '0', '316566', null, '0', '66', '1', '1', null);
