INSERT INTO `fd_reply` VALUES ('16', '24', '13', '现在阅读起来也比以前开心，心里有那么一点觉得读书好像不是什么洪水猛兽，不是那么痛苦的事情了。而且理解起来也不会困难因为我听你的话找的都是很简单的励志类心理啊等简单的来读。', '5', '1');
